package study4;

public class Arithmeticoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int x = 6;
      int y = 3;
      System.out.println("x+y=" + (x+y));
      System.out.println("x-y=" + (x-y));
      System.out.println("x*y=" + (x*y));
      System.out.println("x/y=" + (x/y));
      System.out.println("x%y=" + (x%y));
	}

}
